
no=""

#Converting 1000 digit number to integer list
p=list(str(no))
for i in range(len(p)):
    p[i]=int(p[i])

#Finding all indexes containing zero
z=[]
for i in range(1000):
    if(p[i]==0):
        z.append(i)

#Making an array of indexes which shouldn't be used in product
r=[]
for i in z:
    for t in range(-12,1):
        r.append(i+t)

#Removing duplicates if any in list r[]
r2 = []
for word in r:
    if word not in r2:
        r2.append(word)

m=0   #Initalizing array counter
pl=[] #products array

for i in range(1000-13):
    if(i==r2[m]):                   #if in array r[] don't count product
        m=m+1
    else:
        product=1
        for o in range(13):
            product=product*p[i+o]
        pl.append(product)
pl.sort(reverse=True)
print(pl[0])

#Result: 23514624000
